import React, { useState } from 'react';
import axios from 'axios';
import './SignUp.css';
import signup from './Image/Log2.jpg'; // gives image path

function Registration() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();


    axios.post('/api/register', { username, password, email })
      .then(response => {
        // Handle successful registration
      })
      .catch(error => {
        // Handle registration error
      });
  }

  return (
    <div
      style={{
        backgroundImage: `url(${signup})`,
        backgroundSize: 'cover',
        height: '92vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <table align='center'>
        <div className='signup'>
          <form className='needs-validation' onSubmit={handleSubmit}>
            <h2 className="mb-3">Sign Up</h2>
            <div className='form-group was-validated mb-2'>
              <label htmlfor='Name' className='form-label'>Enter Your Name</label><br />
              <input wrapperClass='mb-4' label='Your Name' size='lg' id='form1' type='text'/>
            </div>

            <div className='form-group was-validated mb-2'>
              <label htmlfor='email' className='form-label'>Email</label><br />
              <input wrapperClass='mb-4' label='Your Email' size='lg' id='form2' type='email'/>
            </div>

            <div className='form-group was-validated mb-2'>
              <label htmlfor='password' className='form-label'>Password</label><br />
              <input wrapperClass='mb-4' label='Password' size='lg' id='form3' type='password' />
            </div>

            <div className='form-group was-validated mb-2'>
              <label htmlfor='password' className='form-label'>Re-Enter Password</label><br />
              <input wrapperClass='mb-4' label='Repeat your password' size='lg' id='form4' type='password' />
            </div>

            <button type='submit' className='btn btn-success w-100 mt-2'>Register</button><br /><br />
            <p className="forgot-password text-right">
              <a href="/login">Already have an account?</a>
            </p>

          </form>
        </div>
      </table>
    </div>
  );
}

export default Registration;
